import React, {useState,useEffect} from "react";
 

// useEffect(callback,[deps])
// có 3 trường hợp sử dụng
// 1.useEffect(callback)
// -callback sẽ được gọi lại mỗi khi cập nhật state (setState)
// 2.useEffect(callback,[])
// -callback chỉ được gọi 1 lần khi component mounted vào DOM
// 3.useEffect(callback,[deps])
// --
// Chức năng: side effects (những tác động bên cạnh)
// call API
// Update DOM
// set Interval, setTimeout
// listen event DOM

// input: callback, dependency
// output: void

// Nguyên tắc chung:
// 1. callback luôn được gọi khi component được mounted vào DOM
// 2.component UI sẽ được render -> callback gọi

// const direction = {
//     first: 'f'
// }

function UseEffectWithDeps(){
    // logic
 
    const [postList,setPostList] = useState([])
    const [isFetching,setIsFetching] = useState(false)
    const [page,setPage] = useState(1)
    const [totalPage,setTotalPage] = useState(0)
    const [direction,setDirection] = useState('next')
     
    // const [limitpage,setLimitPage] = useState([])
    useEffect (()=>{
       setIsFetching(true)
        fetch(`https://js-post-api.herokuapp.com/api/posts?_page=${page}`)
      .then(response => response.json())
      .then(result => {
        console.log(result);
        setPostList(result.data)
        setIsFetching(false)
        const {data,pagination} = result;
        let limit =  pagination._limit 
        let totalRows =  pagination._totalRows 
        let totalPage = Number(Math.ceil(totalRows/limit))
        setTotalPage(totalPage)
        // let pagination = new Array(totalPage)
        // setLimitPage(pagination)
      }
      )
      
    },[page])
    const handleFirst = (e)=>{
        e.preventDefault()
        setPage(1)
        setDirection('first')
    }
   
    const handleNext = (e)=>{
        e.preventDefault()
        if(page < totalPage){
            setPage(page + 1)
            setDirection('next')
        }
       
    }
    const handlePrevious = (e)=>{
        e.preventDefault()
       if(page > 1){
        setPage(page -1)
        setDirection('prev')
       }
       
    }

    const handleLast = (e)=>{
        e.preventDefault()
        setPage(totalPage)
        setDirection('last')
    }
    
    // ui
 

    return(
        <div className="">
            <div>
                <ul className="pagination ">
                <li className={`page-item ${page < 1 ? 'disabled':''} ${direction == 'first'? 'active': ''} `}
                ><a href="" className="page-link" onClick={handleFirst}>First</a></li>
                <li className={`page-item ${direction == 'prev'? 'active': ''} ${page <= 1? 'disabled':''}`}><a href="" className="page-link" onClick={handlePrevious}>Previous</a></li>
                <li className={`page-item ${direction == 'next'? 'active': ''}`}><a href="" className="page-link" onClick={handleNext}>Next</a></li>
                <li className={`page-item ${direction == 'last'? 'active': ''}`}><a href="" className="page-link" onClick={handleLast}>Last</a></li>

                </ul>
            </div>
            <h1 >Post List</h1>
            {isFetching ?(<p className="text-danger">Loading...</p>):(
            <div className="row">
               {
                postList.map((post)=>(
                    <div key={post.id} className="col-md-6 mb-2">
                    <div className="card">
                        <img src={post.imageUrl} alt="" />
                        <div className="card-body">
                            <p>{post.description}</p>
                        </div>
                    </div>
                </div>
                ))
               }
            </div>
            )}
            
        </div>
    )
}
export default UseEffectWithDeps;
