import React, {Component} from "react";

class Calculator extends Component{
    constructor(props){
        super(props)
        this.state = {
            num_1 : '',
            num_2 : '',
            result: 0
        }

    }
    handleInputChange = (e) =>this.setState({
        ...this.state,
        [e.target.name]: parseInt(e.target.value)
      })
    handleTotal = () =>{
        this.setState({
            ...this.state,
            result: this.state.num_1 + this.state.num_2
        })
    }
    componentDidUpdate(){
        console.log(this.state.result);
    }
    handleSub = () =>{
        this.setState({
            ...this.state,
            result: this.state.num_1 + this.state.num_2
        })
    }
    handleMulti = () =>{
        this.setState({
            ...this.state,
            result: this.state.num_1 * this.state.num_2
        })
    }
    handleDivide = () =>{
        this.setState({
            ...this.state,
            result: this.state.num_1 / this.state.num_2
        })
    }
    render(){
       return(
        <div>
            <input type="number" onChange={this.handleInputChange} /> <br/>
            <input type="number" onChange={this.handleInputChange} />
            <p>Result:{this.state.result}</p>
            <button onClick={this.handleTotal}>+</button>
            <button>-</button>
            <button>x</button>
            <button>/</button>

        </div>
       )
    }
}

export default Calculator
