import React,{Component} from "react";
import Intro from "./Components/renderIntro.jsx/renderIntro";
 class App extends Component{
  constructor(props){
    super(props)
    this.state = {
      show : false
    }
  }
  toggleIntroduce= ()=>{
    this.setState({
      show: !this.state.show
  })
  }
  componentDidUpdate(){
    console.log(this.state.show);
  }

  render(){
    return(
      <div>
        <h1 style={{backgroundColor:'green', color: 'white',paddingLeft:'40px'}}>Conditional Rendering</h1>
       <Intro onClick = {this.toggleIntroduce} show = {this.state.show}/>
      </div>
    )
  }
 }

export default App;
