import React, {Component} from "react";

class Calculator extends Component{
    constructor(props){
        super(props)
        this.state = {
            num_1 : '',
            num_2 : '',
            total: 0
        }

    }
    handleInputChange = (e)=>{
         console.log(e.target.value);
    }
    
    render(){
       return(
        <div>
            <input type="number" onInput={this.handleInputChange} /> <br/>
            <input type="number" />
            <p>Result:{this.state.total}</p>
            <button>+</button>
            <button>-</button>
            <button>x</button>
            <button>/</button>

        </div>
       )
    }
}

export default Calculator
