import React, {Component} from "react";

class Login extends Component{
    constructor(props){
        super(props)
        this.state = {
            isLogged: false,
            email: '',
            password: ''
        }
    }
    save_data(e){
        this.setState({
            ...this.state,
            [e.target.name] : e.target.value
        })
    }
    valid_data(){
        this.setState({
            isLogged: !this.state.isLogged
        })
    }
    render(){
        return(
            <div className="container d-flex align-item-center text-center">
                <div className="form-signin">
                    <form action="">
                        <img className="mb-4" src="https://upload.wikimedia.org/wikipedia/commons/thumb/b/b2/Bootstrap_logo.svg/2560px-Bootstrap_logo.svg.png" alt="" width={72} height={57} />
                        <h1 className="h3 mb-3 fw-normal">Please sign in</h1>
                        <div className="form-floating">
                            <input className="form-control email" type="email" name="email" placeholder="name@example.com" onChange={this.save_data} />
                            <label htmlFor="">Email address</label>
                        </div>
                        <div className="form-floating">
                            <input className="form-control password" type="password" name="password"   />
                            <label htmlFor="">Password</label>
                        </div>
                        <div className="checkbox mb-3">
                            <label htmlFor="">
                              <input type="checkbox" onChange={this.props.remember} />Remember me
                            </label>
                        </div>
                        <button className="w-100 btn btn-lg btn-primary" type="button" onClick={this.valid_data}>Sign in</button>
                        <p className="mt-5 mb-3 text-muted">© 2017–2021</p>
                    </form>
                </div>
            </div>
        )
    }
}
export default Login;
