import React ,{Component}from "react";

class Intro extends Component{
    render(){
        if(this.props.show){
            return(
                <>
                <button onClick={this.props.onClick}>Đóng giới thiệu</button>
                <p>Lorem ipsum, dolor sit amet consectetur
                     adipisicing elit. Quod, corporis.</p>
                </>
            )
            
        }
        return(
            <button onClick={this.props.onClick}>Giới thiệu</button> 
        )
    }
}
export default Intro;
